    private void unLockAppBar() {
        ViewCompat.setNestedScrollingEnabled(nestedScrollView, true);

        CoordinatorLayout.LayoutParams params = (CoordinatorLayout.LayoutParams) mAppBarLayout.getLayoutParams();
