    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        recyclerView = view.findViewById(R.id.recyclerViewImageItems)
        galleryNumberText = view.findViewById(R.id.galleryNumber)

        // ...
    }
}
```

