    public static void reverse(int[] data) {
        int left = 0;
        int right = data.length - 1;
		
        while( left &lt; right ) {
            // swap the values at the left and right indices
            int temp = data[left];
            data[left] = data[right];
            data[right] = temp;
			
            // move the left and right index pointers in toward the center
            left++;
            right--;
        }
    }

