    public class ReverseIterator&lt;T&gt; implements Iterator&lt;T&gt; {
      private int index;
      private final List&lt;T&gt; list;
      public ReverseIterator(List&lt;T&gt; list) {
         this.list = list;
         this.index = list.size() - 1;
      }
      public boolean hasNext() {
        return index &gt;= 0 ? true : false;
      }
      public T next() {
        if(index &gt;= 0) 
