    {&#39; &#39;, &#39;B&#39;, &#39;R&#39;, &#39; &#39;, &#39; &#39;},
    {&#39; &#39;, &#39; &#39;, &#39;E&#39;, &#39;B&#39;, &#39; &#39;}
};
char[] v = new char[] { &#39;B&#39;, &#39;R&#39;, &#39;A&#39;, &#39;V&#39;, &#39;E&#39; };
