            System.arraycopy(encrypted, 0, combined, iv.length, encrypted.length);

            // Return the combined data as a Base64 encoded string
            return android.util.Base64.encodeToString(combined, android.util.Base64.DEFAULT);
        } catch (Exception e) {
            throw new RuntimeException(&quot;Error while encrypting&quot;, e);
        }
    }

    public static String decryptData(String secretKey, String encryptedContent) {
        try {
            SecretKeySpec secretKeySpec = getSecretKeySpec(secretKey);

            // Decode the Base64 encoded string
            byte[] combined = android.util.Base64.decode(encryptedContent, android.util.Base64.DEFAULT);

            // Extract the IV and encrypted content
            byte[] iv = new byte[16];
            byte[] encrypted = new byte[combined.length - 16];

            System.arraycopy(combined, 0, iv, 0, 16);
            System.arraycopy(combined, 16, encrypted, 0, encrypted.length);
            IvParameterSpec ivSpec = new IvParameterSpec(iv);

            // Decrypt
            Cipher cipher = Cipher.getInstance(&quot;AES/CBC/PKCS5Padding&quot;);
            cipher.init(Cipher.DECRYPT_MODE, secretKeySpec, ivSpec);
            byte[] decrypted = cipher.doFinal(encrypted);

            // Convert the decrypted byte array back to a string
            return new String(decrypted, StandardCharsets.UTF_8);
        } catch (Exception e) {
            throw new RuntimeException(&quot;Error while decrypting&quot;, e);
        }
    }
