    private String[][] transformToRows(final List&lt;String[]&gt; columns) {
        final String[][] dataMatrix = new String[rowSize][HEADERS.length];

        for (int i = 0; i &lt; columns.size(); i++) {
            for (int j = 0; j &lt; rowSize; j++) {
