                    System.out.println(&quot;Customer &quot; + customer.getID()
                            + &quot; joined queue &quot; + (shortestLineIndex));
                } else {
                    System.out.println (customer 
                         + &quot; got frustrated and left the store &quot; 
                         + &quot;without checking out!&quot;);
                }
            }

            for (int lane = 0; lane &lt; checkoutLines.size (); lane++) {
                Customer customer = checkoutLines.get(lane).peek();
                   if (customer == null) { continue; } // lane empty
                   if (customer.tick() &lt;= 0) { 
                      System.out.println (customer + &quot;is checking out from queue &quot;
                            + lane);
                      checkoutLines.get(lane).remove();
                }
            }                     
        }
