        SomeBean someBean = new SomeBean(&quot;value1&quot;, &quot;value2&quot;, &quot;value3&quot;);
    
        SimpleBeanPropertyFilter filter = SimpleBeanPropertyFilter.filterOutAllExcept(&quot;field1&quot;, &quot;field2&quot;);
    
