		private void initUI() {
			Toolkit.getDefaultToolkit().addAWTEventListener(windowListener, AWTEvent.WINDOW_EVENT_MASK);
			JOptionPane.showConfirmDialog(null, &quot;Here is a simple confirm dialog&quot;, &quot;Confirm!&quot;, JOptionPane.YES_NO_CANCEL_OPTION);
