            }
            // Otherwise...
            commandLine = &quot;&quot;;       // Empty `commandLine` so to ensure re-loop.
            System.out.println();   // Display a blank line for aesthetics.
        }
    }
    
    /**
     * This method prompts the User to ensure that he/she truly wants to 
     * quit the application. 
     * &lt;p&gt;
     * If `n` (no) is supplied, then boolean false is returned and this 
     * then gets interpreted that quit is not desired and the application 
     * continues to prompt for, and accept, Vector entries.
     * &lt;p&gt;
     * If `y` (yes) is supplied then the User is confronted with yet 
     * another prompt which asks if he/she wants to display the stored 
     * Vector entries. If at this point `n` (no) is supplied then the 
     * application will simply return boolean true which is later interpreted 
     * as a desire to quit. If however, `y` (yes) is supplied, then 
     * the User is displayed all the stored vector entries and then the 
     * application simply returns true which is also later interpreted as 
     * a desire to quit.&lt;br&gt;&lt;br&gt;
     * 
     * @return (boolean) 
     */
    private boolean quit() {
        boolean res = false;
        // Prompt #2 - Prompt for `Are You Sure...`:
        String yn = &quot;&quot;;
        while (yn.isEmpty()) {
            System.out.print(&quot;Are you sure you want to quit (y/n)? -&gt; &quot;);
            yn = userInputScanner.nextLine().trim();
            if (!yn.matches(&quot;(?i)[yn]&quot;)) {
                System.err.println(&quot;Invalid Entry Response! Try again...&quot; + LS);
                yn = &quot;&quot;;
            }
        }
        if (yn.equalsIgnoreCase(&quot;y&quot;)) {
            // Prompt #3 - Prompt to display store Vector entries but only if there are any: 
            yn = &quot;&quot;;
            while (yn.isEmpty() &amp;&amp; !coordsList.isEmpty()) {
                System.out.print(&quot;Do you want to display all User Entered Vectors first? -&gt; &quot;);
                yn = userInputScanner.nextLine().trim();
                if (!yn.matches(&quot;(?i)[yn]&quot;)) {
                    System.err.println(&quot;Invalid Entry Response! Try again...&quot; + LS);
                    yn = &quot;&quot;;
                }
            }
            if (yn.equalsIgnoreCase(&quot;y&quot;)) {
                System.out.println(LS + &quot;Vectors Entered By User:&quot; + LS +
                                        &quot;------------------------&quot;);
                for (VectorCoordinate c : coordsList) {
                    System.out.printf(&quot;X = %-10s Y = %-10s%n&quot;, c.getX(), c.getY());
                }
                System.out.println(&quot;------------------------&quot;);
            }
            System.out.println(LS + &quot;Quiting - Bye Bye.&quot; + LS);
            res = true;
        }
        return res;
    }
