class Rectangulo2 {
	private static final Stroke STROKE = new BasicStroke(3f);
	private Color color;
	private Point p0, p1;

	public Rectangulo2() {
	}

	public Rectangulo2(Color color, Point p0, Point p1) {
		super();
		this.color = color;
		this.p0 = p0;
		this.p1 = p1;
	}

	public Color getColor() {
		return color;
	}

