		int x = Math.min(p0.x, p1.x);
		int y = Math.min(p0.y, p1.y);
		int width = Math.abs(p0.x - p1.x);
		int height = Math.abs(p0.y - p1.y);
		g2.fillRect(x, y, width, height);

		g2.setStroke(STROKE);
		g2.setColor(Color.BLACK);
		g2.drawRect(x, y, width, height);
		g2.dispose();
	}

}
