        if(train.isLightOnInCurrentWagon()){
          goBack(train,count);
          count++;
        }else{
        
           train.switchLight();
           goBack(train,count);
           
           if(train.isLightOnInCurrentWagon()){
             end=true;
           }else{
           
             //we need to reset the ligt state
             goFoward(train,count);
             train.switchLight();
             goBack(train,count);
             count++;
             
           }
        }
        
        //we need to reset to the initial state
