         	&lt;artifactId&gt;maven-javadoc-plugin&lt;/artifactId&gt;
         	&lt;version&gt;3.6.0&lt;/version&gt;
         	&lt;executions&gt;
                   &lt;execution&gt;
                     &lt;id&gt;attach-javadocs&lt;/id&gt;
                     &lt;goals&gt;
                         &lt;goal&gt;jar&lt;/goal&gt;
                     &lt;/goals&gt;
                   &lt;/execution&gt;
            &lt;/executions&gt;
            &lt;configuration&gt;
