		Point p = getLocation();
		if ((p.x + p.y) % 2 == 0) {
			gg.setColor(Color.blue);
		} else {
			gg.setColor(Color.cyan);
		}
		// paint this component so location is always 0,0.
		// all you&#39;re doing is painting this squares background
		gg.fillRect(0,0, this.getWidth(), this.getHeight());
		gg.dispose();
	}
}

