        public Toast(JFrame owner, String toastText) {
            setTitle(&quot;Transparent JFrame Demo&quot;);
            setLayout(new GridBagLayout());
    
            setDefaultCloseOperation(EXIT_ON_CLOSE);
            setUndecorated(true);
