    class FileHandler {
        boolean flag = false;
        int count;
        void countSpace(File file) {
            try (Scanner sc = new Scanner(file)) {
                synchronized (this){
                    wait();
                    while (sc.hasNext() &amp;&amp; !flag) {
                        count++;
                        sc.next();
                    }
                    flag = true;
                    notify();
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    
        void changeLetters(File file) throws InterruptedException {
