                f.format(&quot;[4] Division%n&quot;);
                f.format(&quot;[5] Greater Than/Less Than%n&quot;);
                f.format(&quot;[6] Return%n&quot;);
                f.format(&quot;Enter the number you chose:&quot;).flush();
