                                case -1 -&gt; &quot;lesser-than &quot;;
                                case 0 -&gt; &quot;equal-to &quot;;
                                default -&gt; &quot;greater-than &quot;;
                            });
                        f.format(b.toPlainString()).flush();
                    }
                    case 6 -&gt; {
                        System.out.println(&quot; \nThank you and Goodbye!&quot;);
                        exit = true;
                    }
                    default -&gt; {
                        x = new BigDecimal(
                                String.valueOf(
                                    switch (i) {
                                        case 1 -&gt; a.add(b);
