                        String op = switch (i) {
                            case 1 -&gt; &quot;+&quot;;
                            case 2 -&gt; &quot;-&quot;;
                            case 3 -&gt; &quot;*&quot;;
                            default -&gt; &quot;/&quot;;
                        };
                        f.format(a.toPlainString());
