import org.apache.pdfbox.pdmodel.PDDocument;
import org.apache.pdfbox.pdmodel.PDPage;
import org.apache.pdfbox.pdmodel.PDPageContentStream;
import org.apache.pdfbox.pdmodel.font.PDFont;
import org.apache.pdfbox.pdmodel.font.PDType1Font;

// Method to add a paragraph to a PDF document
public static void addParagraph(PDDocument document, String text, PDFont font, float fontSize, float margin) throws IOException {
  // Create a new page if the document is empty
  if (document.getNumberOfPages() == 0) {
    document.addPage(new PDPage());
  }
  
  // Get the current page and its content stream
  PDPage page = document.getPage(document.getNumberOfPages() - 1);
  PDPageContentStream contentStream = new PDPageContentStream(document, page, PDPageContentStream.AppendMode.APPEND, true);
  
  // Set the text attributes
  contentStream.setFont(font, fontSize);
  contentStream.setLeading(fontSize * 1.2f); // Line spacing
  
  // Split the text into lines based on the page width and margin
  float pageWidth = page.getMediaBox().getWidth();
  float maxLineWidth = pageWidth - 2 * margin;
  List&lt;String&gt; lines = new ArrayList&lt;&gt;();
  int lastSpace = -1;
  while (text.length() &gt; 0) {
    int spaceIndex = text.indexOf(&#39; &#39;, lastSpace + 1);
    if (spaceIndex &lt; 0) {
      spaceIndex = text.length();
    }
    String subString = text.substring(0, spaceIndex);
    float subStringWidth = font.getStringWidth(subString) / 1000 * fontSize;
    if (subStringWidth &gt; maxLineWidth) {
      if (lastSpace &lt; 0) { // The word is longer than the line
        lastSpace = spaceIndex; // Break the word
      }
      subString = text.substring(0, lastSpace);
      lines.add(subString);
      text = text.substring(lastSpace).trim();
      lastSpace = -1;
    } else if (spaceIndex == text.length()) { // The last line
      lines.add(text);
      text = &quot;&quot;;
    } else {
      lastSpace = spaceIndex;
    }
  }
  
  // Write the lines to the content stream
  contentStream.beginText();
  float pageHeight = page.getMediaBox().getHeight();
  float currentY = pageHeight - margin; // Start from the top margin
  for (String line : lines) {
    float lineWidth = font.getStringWidth(line) / 1000 * fontSize;
    float currentX = margin + (maxLineWidth - lineWidth) / 2; // Center the text horizontally
    if (currentY - fontSize &lt; margin) { // No more space in the current page
      contentStream.endText();
      contentStream.close();
      page = new PDPage(); // Create a new page
      document.addPage(page);
      contentStream = new PDPageContentStream(document, page, PDPageContentStream.AppendMode.APPEND, true); // Get its content stream
      contentStream.setFont(font, fontSize); // Set the text attributes again
      contentStream.setLeading(fontSize * 1.2f);
      contentStream.beginText();
      currentY = pageHeight - margin; // Reset the current Y position
    }
    contentStream.newLineAtOffset(currentX, currentY); // Move to the next line position
    contentStream.showText(line); // Show the line
    contentStream.newLine(); // Advance to the next line
    currentY -= fontSize * 1.2f; // Update the current Y position
  }
  
  // Close the content stream and save the document
  contentStream.endText();
  contentStream.close();
}

