    public class BOMSkipper
    {
        public static void skip(Reader reader) throws IOException
        {
            reader.mark(1);
            char[] possibleBOM = new char[1];
            reader.read(possibleBOM);
    
            if (possibleBOM[0] != &#39;\ufeff&#39;)
            {
                reader.reset();
            }
        }
    }


