      State.addTransition(s0, s2, Type.A); // s0 ---a---&gt; s2
      State.addTransition(s1, s3, Type.A); // s1 ---a---&gt; s3
      State.addTransition(s2, s3, Type.A); // s2 ---a---&gt; s3
   
   }

}

```

