    public static void countSwaps(List&lt;Integer&gt; a) {
        int count = 0;
        boolean flag = false;
        while (!flag) {
            flag = true;
            for (int i = 0; i &lt; a.size() - 1; i++) {
                if (a.get(i) &gt; a.get(i + 1)) {
                    int temp = a.get(i);
                    a.set(i, a.get(i + 1));
                    a.set(i + 1, temp);
