                return Files.lines(p, Charset.defaultCharset()).onClose(() -&gt; {
                    try { // move path/x/y/z to path/x/y/z.moved
                        Files.move(p, p.resolveSibling(p.getFileName()+&quot;.moved&quot;));
                    } catch(IOException ex) { throw new UncheckedIOException(ex); }
