				x.addActionListener(e -&gt; {
					final int[] selectedRows = this.table.getSelectedRows();
					for (final int row : selectedRows) {
						highlightedRows.put(row, color);
					}
				});
			}
			
			return highlightMenu;
		}
	
		private JTable createTable() {
