        if (!map.containsKey(indices[i]))
            map.put(indices[i], new ArrayList&lt;&gt;());
        map.get(indices[i]).add(arr[i]);
    }

    int i = 0;

    for (List&lt;Integer&gt; values : map.values())
        for (int value : values)
            arr[i++] = value;
}
