        FutureTask&lt;Object&gt; futureTask = new FutureTask&lt;&gt;(callable);
        future = cache.putIfAbsent(x, futureTask);
        if (future == null) {
          future = futureTask;
          futureTask.run();
        }
      }
      try {
        return future.get();
      } catch (CancellationException e) {
        cache.remove(x);
      } catch (ExecutionException e) {
        throw new RuntimeException(e.getCause());
      }
    }
  }
