    companion object {
        var isCapsEnabled = false
    }

    private var isBackspaceKeyPressed = false
    private val handler = Handler(Looper.getMainLooper())
    private var deleteCharacterRunnable = object : Runnable {
        override fun run() {
            val inputConnection = currentInputConnection

            if (inputConnection != null &amp;&amp; isBackspaceKeyPressed) {
                val selectedText: CharSequence? = inputConnection.getSelectedText(0)

                if (selectedText != null &amp;&amp; !TextUtils.isEmpty(selectedText)) {
                    // There is selected text, so delete it
                    inputConnection.commitText(&quot;&quot;, 1)
                } else {
                    // No selected text, delete the character to the left
                    inputConnection.deleteSurroundingText(1, 0)
                }

                // Schedule the next deletion after 100 ms
                handler.postDelayed(this, 100)
            }
        }
    }

    override fun onCreateInputView(): View {
        val view = ComposeKeyboardView(this)

        window!!.window!!.decorView.let { decorView -&gt;
