            view.setViewTreeSavedStateRegistryOwner(this)
        }
        view.let {
            ViewTreeLifecycleOwner.set(it, this)
            ViewTreeViewModelStoreOwner.set(it, this)
            it.setViewTreeSavedStateRegistryOwner(this)
        }
        return view
    }


    fun doSomethingWith(
        key: Key,
        isLongPressed: Boolean
    ) {
        when (key.label) {
            &quot;Space&quot; -&gt; {
                if (isLongPressed) {
                    val imm = getSystemService(INPUT_METHOD_SERVICE) as InputMethodManager
                    imm.showInputMethodPicker()
                } else {
                    currentInputConnection.commitText(&quot; &quot;, 1)
                }
            }

            &quot;CAPS&quot; -&gt; {
                isCapsEnabled = !isCapsEnabled
            }

            &quot;Done&quot; -&gt; {
                currentInputConnection.sendKeyEvent(
                    KeyEvent(
                        KeyEvent.ACTION_DOWN,
                        KeyEvent.KEYCODE_ENTER
                    )
                )
            }

            &quot;Delete&quot; -&gt; {
                if (isLongPressed) {
                    // Start character deletions when the key is long-pressed
                    if (!isBackspaceKeyPressed) {
                        isBackspaceKeyPressed = true
                        deleteCharacterRunnable.run()
                    }
                } else {
                    val inputConnection = currentInputConnection

                    if (inputConnection != null) {
                        val selectedText: CharSequence? = inputConnection.getSelectedText(0)

                        if (selectedText != null &amp;&amp; !TextUtils.isEmpty(selectedText)) {
                            // There is selected text, so delete it
