			return this;
		}
		
		ByteBuffer outputBuffer = ByteBuffer.allocate(width * height * CHANNELS);
		
		for (int x = 0; x &lt; width; x++) {
			for (int y = 0; y &lt; height; y++) {
				int offset = NNGINEUtil.offset(x, y, width, CHANNELS);
				
				int flipX = (flipHorizontally ? (width - 1) - x : x);
				int flipY = (flipVertically ? (height - 1) -  y: y);
				
				int invOffset = NNGINEUtil.offset(flipX, flipY, width, CHANNELS);
				
				for (int i = 0; i &lt; CHANNELS; i++) {
					outputBuffer.put(offset + i, imageBuffer.get(invOffset + i));
				}
			}
		}
		
		imageBuffer = outputBuffer;
		
		return this;
	}

