		double mt = height*(0.0254); // converting inches to meters
		double BMI = kg/(mt*mt); // calculating BMI
		return BMI;
	}
	
	public static String interpretBMI(double bmi) {
		
		if(bmi &lt; 18.5)
			return &quot;Underweight&quot;;
		else if(18.5 &lt;= bmi &amp;&amp; bmi &lt;= 25.0)	
			return &quot;Normal&quot;;
		else if(25.0 &lt;= bmi &amp;&amp; bmi &lt;= 30.0)	
			return &quot;Overweight&quot;;
		else if(30.0 &lt;= bmi)			
			return &quot;Obese&quot;;
		else
			return &quot;Error&quot;;
	}
}
