    public static &lt;T&gt; Set&lt;T&gt; getDuplicates(Collection&lt;T&gt; array) {
        Set&lt;T&gt; duplicateItem = new HashSet&lt;&gt;();
        Iterator&lt;T&gt; it = array.iterator();
        while(it.hasNext()) {
            T object = it.next();
            if (Collections.frequency(array, object) &gt; 1) {
                duplicateItem.add(object);
                it.remove();
            }
        }
        return duplicateItem;
    } 

