          return;
        } catch (Exception e) {
          logger.error(&quot;Class [{}] not found with Class.forName&quot;);
        }
    
        // Otherwise, iterate all loaded classes and find what we want.
        for (Class&lt;?&gt; clazz : instrumentation.getAllLoadedClasses()) {
