      public &lt;T extends Comparable&lt;T&gt;&gt; void sort(T[] array) {
        for (int i = 0; i &lt; array.length - 1; ++i) {
          boolean swapped = false;
          for (int j = 0; j &lt; array.length - 1 - i; ++j) {
            if (array[j].compareTo(array[j + 1]) &gt; 0) {
