```java
public static int[] joinArrays(int[] a, int[] b) {
    int[] shorter, bigger;
    if (b.length &lt; a.length) {
        shorter = b;
        bigger = a;
    } else {
        shorter = a;
        bigger = b;
    }
    for (int i = 0; i &lt; shorter.length; i++) {
        bigger[i] += shorter[i];
    }
    return bigger;
}
