    			DefaultTableModel model = (DefaultTableModel)table.getModel();
    			for (int i = model.getRowCount() -1; i &gt;= 0; i--) {
    				Object[] row = getNewRow();
    				model.setValueAt(row[0], i, 0);
    				model.setValueAt(row[1], i, 1);
    			}	
    		}
    	}
    
