                    // require authentication for all other cases
                    c.anyRequest().authenticated();
                })
                .oauth2Login(c -&gt; {
                    c.authorizationEndpoint(auth -&gt; auth.authorizationRequestResolver(resolver)); // use PKCE
                })
                .build();
    }

