    private void moveOrRemoveCircle(Circle circle, Map&lt;Circle, Integer&gt; circleMap, GridPane gridPane) {
        Integer currentRow = circleMap.get(circle);
        if (currentRow == null || !canMoveToNextRow(circle, circleMap, currentRow)) {
