  &lt;AD id=&quot;DataSourceFactory.target&quot;          type=&quot;String&quot;  default=&quot;(service.pid=${dataSourceRef})&quot; ibm:final=&quot;true&quot; name=&quot;internal&quot; description=&quot;internal use only&quot;/&gt;
