    new Timer().schedule(new TimerTask() {
      @Override
      public void run() {
        testAsync.test();
      }
    }, 1000);

    verify(testAsync, timeout(2000).times(0)).test();

  }

  @Test
  void testAsyncWaitOnce() {

    var testAsync = Mockito.mock(TestAsync.class);
    doCallRealMethod().when(testAsync).test();

    new Timer().schedule(new TimerTask() {
      @Override
      public void run() {
        testAsync.test();
      }
