        // set color and area of shape
        g.setColor(new Color(0, 255, 0));

        // checks array every repaint for modified tiles
        for (int x = 0; x &lt; h; x++) {
            for (int y = 0; y &lt; w; y++) {
                int i = y + (x * w);
                if (tiles[i] == 1) {
                    tilex = x * tileSize;
                    tiley = y * tileSize;
                    g.fillRect(tilex, tiley, tileSize, tileSize);
                }
            }
        }
    }

