public static int toDecimal(String hex) {
		String hexstring = &quot;0123456789ABCDEF&quot;;
		hex = hex.toUpperCase();
		int num = 0;
		for (int i = 0; i &lt; hex.length(); i++) {
			char ch = hex.charAt(i);
