                if (mPtr &gt;= 0 &amp;&amp; nums1[mPtr] &gt;= nums2[nPtr]) {
                    nums1[index--] = nums1[mPtr--];
                } else {
                    nums1[index--] = nums2[nPtr--];
                }
            }
        }
    }

