	public boolean didWin(int[][] grid, int check, int row, int col, int rowDelta, int colDelta) {

		boolean win = true;
		for (int count = 0; count &lt; 4; count++) {
			if (row &lt; ROWS &amp;&amp; row &gt;= 0 &amp;&amp; col &lt; COLUMNS &amp;&amp; col &gt;= 0) {
				int test = grid[row][col];
				if (test != check) {
					win = false;
					break;
				}
			}
			row += rowDelta;
			col += colDelta;
