        }
    }

    private static void dfs(int u, Graph g, int[] disc, int[] low, int[] parent,
                            List&lt;Pair&lt;Integer, Integer&gt;&gt; result,int time) {
        disc[u] = low[u] = time;
        time++;
        for(int v : g.getVertices(u)){
            if(disc[v] == -1){
                parent[v] = u;
                dfs(v,g,disc,low,parent,result,time);
