            &quot;-...-&quot;, &quot;-....-&quot;, &quot;.-.-.&quot;, &quot;.-..-.&quot;, &quot;-..-.&quot;, &quot;.-...&quot;, &quot;.----.&quot;,
            &quot;-.--.&quot;, &quot;-.--.-&quot;
        };
    
        private static final Map&lt;String, String&gt; EN_TO_MORSE = new HashMap&lt;&gt;();
        private static final Map&lt;String, String&gt; MORSE_TO_EN = new HashMap&lt;&gt;();
    
        static {
            for (int i = 0; i &lt; english.length; i++) {
