				&lt;xs:enumeration value=&quot;3.1&quot;/&gt;
				&lt;xs:enumeration value=&quot;3.0&quot;/&gt;
			&lt;/xs:restriction&gt;
		&lt;/xs:simpleType&gt;
	&lt;/xs:attribute&gt;
&lt;/xs:complexType&gt;
