    	public static int[] removeNegativeNumbers(int[] num) {
    		 int[] output = new int[num.length];
    		 int k = 0;
    		 int i = 0; 
    		 int last=-1;
    		 int howmany=0;
    		 
    		 while(i &lt; num.length) {
    		 	 if(num[i] &lt; 0) {
    	 	 		howmany=i-last-1;
    	 	 		switch(howmany) {
    	 	 			case 0: break;
    	 	 			case 1: 
    	 	 					output[k]=num[last+1];
    	 	 					k++;
    	 	 					break;
    	 	 			default:		
    	 	 					System.arraycopy(num, last+1,  output, k, howmany);
    	 	 					k+=howmany;
    	 	 		}
    				last=i;
    		 	 } 
